import React, { Component } from 'react';
import { connect } from 'react-redux'

import Grid from '@material-ui/core/Grid';
import CssBaseline from '@material-ui/core/CssBaseline';
import { Container, Typography } from '@material-ui/core';

import TableList from '../../components/table/Table';
import Modal from '../../components/modalExpense/modal'

import { getCollection } from '../../providers/firebaseProvider';


class ListExpenses extends Component {
    constructor(props) {
        super(props);
        this.state = {
            expenses: [],
            is_open: true
        }
    }

    async componentDidMount() {
        await getCollection('expenses', async ({ docs }) => {
            const items = docs.map((doc) => ({ ...doc.data() }))
            this.setState({ ...this.state, expenses: items })
        })
    }

    handleChange() {
        this.setState({ ...this.state, is_open: !this.state.is_open })
    }

    render() {
        const { expenses, is_open } = this.state
        return (
            <Container style={{ height: '100vh' }}>
                <CssBaseline />
                <Grid
                    container
                    direction="row"
                    justify="center"
                    alignItems="top"
                // style={{ height: '100%' }}
                >
                    <TableList items={expenses} openModal={() => this.handleChange()} />
                    <Modal is_open={is_open} close={() => this.handleChange()} />
                </Grid>
            </Container>
        )
    }
}


const mapStateToProps = (state) => {
    return {
        expenses: state.firestore.ordered.expenses,
    }
}


export default connect(mapStateToProps)(ListExpenses)
