import React, { Component } from 'react';
import { connect } from 'react-redux'
import { firestoreConnect } from 'react-redux-firebase'
import { compose } from 'redux'

import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CssBaseline from '@material-ui/core/CssBaseline';
import { Container, Typography } from '@material-ui/core';

import TableList from '../components/Table';

import { getCollection } from '../providers/firebaseProvider';


class ListExpenses extends Component {
    constructor(props) {
        super(props);
        this.state = {
            expenses: []
        }
    }

    async componentDidMount() {
        await getCollection('expenses', async ({ docs }) => {
            const items = docs.map((doc) => ({ ...doc.data() }))
            this.setState({...this.state, expenses: items})
        })
    }

    render() {
        const { expenses } = this.state
        return (
            <Container style={{ height: '100vh' }}>
                <CssBaseline />
                <Grid
                    container
                    direction="row"
                    justify="center"
                    alignItems="top"
                // style={{ height: '100%' }}
                >
                    <TableList items={ expenses } />
                </Grid>
            </Container>
        )
    }
}


const mapStateToProps = (state) => {
    return {
        expenses: state.firestore.ordered.expenses,
    }
}


export default connect(mapStateToProps)(ListExpenses)
