import React, { Component } from 'react';
import { connect } from 'react-redux'

import Grid from '@material-ui/core/Grid';
import CssBaseline from '@material-ui/core/CssBaseline';
import { Container, Typography } from '@material-ui/core';

import TableList from '../../components/table/Table';
import Modal from '../../components/modalExpense/Modal'

import { getCollection, deleteExpense, updateExpense } from '../../providers/firebaseProvider';


class ListExpenses extends Component {
    constructor(props) {
        super(props);
        this.state = {
            expenses: [],
            is_open: false,
            item_selected: {}
        }
    }

    async componentDidMount() {
        await getCollection('expenses', async ({ docs }) => {
            const items = docs.map((doc) => ({ ...doc.data(), id: doc.id }))
            this.setState({ ...this.state, expenses: items })
        })
    }

    handleModalChange(item) {
        this.setState({
            ...this.state,
            is_open: !this.state.is_open,
            item_selected: item ? item : {}
        })
    }

    handleChange = (e) => {
        this.setState({
            ...this.state.item_selected[e.target.id] = e.target.value
        })
    }

    async deleteItem(id) {
        await deleteExpense(id)
    }
    async updateItem(e) {
        e.preventDefault();
        const { item_selected } = this.state
        await updateExpense(item_selected)
    }
    render() {
        const { expenses, is_open, item_selected } = this.state
        return (
            <Container style={{ height: '100vh' }}>
                <CssBaseline />
                <Grid
                    container
                    direction="row"
                    justify="center"
                    alignItems="top"
                >
                    <TableList items={expenses}
                        openModal={(item) => this.handleModalChange(item)}
                        deleteItem={(id) => this.deleteItem(id)}
                    />
                    <Modal is_open={is_open}
                        close={() => this.handleModalChange()}
                        item={item_selected}
                        handleChange={(e) => this.handleChange(e)}
                        save={(e)=>this.updateItem(e)}
                    />
                </Grid>
            </Container>
        )
    }
}


const mapStateToProps = (state) => {
    return {
        expenses: state.firestore.ordered.expenses,
    }
}


export default connect(mapStateToProps)(ListExpenses)
