import React, { Component } from 'react';
import { connect } from 'react-redux'

import Grid from '@material-ui/core/Grid';
import CssBaseline from '@material-ui/core/CssBaseline';
import { Container, Typography } from '@material-ui/core';

import TableList from '../../components/table/Table';
import Modal from '../../components/modalExpense/modal'

import { getCollection } from '../../providers/firebaseProvider';


class ListExpenses extends Component {
    constructor(props) {
        super(props);
        this.state = {
            expenses: [],
            is_open: false,
            item_selected: {
                description:'',
                value: 0,
                paid: false,
                date:''
            }
        }
    }

    async componentDidMount() {
        await getCollection('expenses', async ({ docs }) => {
            const items = docs.map((doc) => ({ ...doc.data() }))
            this.setState({ ...this.state, expenses: items })
        })
    }

    handleModalChange(item) {
        this.setState({ ...this.state,
            is_open: !this.state.is_open, 
            item_selected: item ? item : {} })
    }

    handleChange = (e) => {
        console.log(e.target)
        this.setState({
            ...this.state.item_selected[e.target.id] = e.target.value
        })
    }

    render() {
        const { expenses, is_open, item_selected } = this.state
        return (
            <Container style={{ height: '100vh' }}>
                <CssBaseline />
                <Grid
                    container
                    direction="row"
                    justify="center"
                    alignItems="top"
                // style={{ height: '100%' }}
                >
                    <TableList items={expenses} openModal={(item) => this.handleModalChange(item)} />
                    <Modal is_open={is_open} 
                        close={() => this.handleModalChange()} 
                        item={item_selected} 
                        handleChange={(e)=>this.handleChange(e)}
                        />
                </Grid>
            </Container>
        )
    }
}


const mapStateToProps = (state) => {
    return {
        expenses: state.firestore.ordered.expenses,
    }
}


export default connect(mapStateToProps)(ListExpenses)
