import React from 'react'
import Modal from '@material-ui/core/Modal';
import FormControl from '@material-ui/core/FormControl';
import Typography from '@material-ui/core/Typography';
import Input from '@material-ui/core/Input';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Button from '@material-ui/core/Button'
import Paper from '@material-ui/core/Paper'
import TextField from '@material-ui/core/TextField';
import DateFnsUtils from '@date-io/date-fns';
import {
    MuiPickersUtilsProvider,
    KeyboardTimePicker,
    KeyboardDatePicker,
} from '@material-ui/pickers';
import moment from 'moment'

const ModalEdit = ({ is_open, close, item, handleChange }) => (
    <Modal
        open={is_open}
        onClose={() => close()}
        style={{ display: 'flex', alignItems: 'center', padding: '20vw' }}
    >
        <Paper style={{
            width: '100%',
            marginTop: 15,
            // padding: 20,
            overflowX: 'auto'
        }}>
            <Typography variant="h6" id="tableTitle" style={{ padding: 15, borderBottom: "solid 1px #dedede" }}>
                Edição de Despesas
        </Typography>
            <form style={{ margin: 40, marginTop: 15, display: 'flex', flexDirection: "column" }}>
                <TextField
                    label="Descrição"
                    id="description"
                    fullWidth
                    onChange={(e) => handleChange(e)}
                    value={item.description}
                />
                <FormControl style={{ marginTop: 25 }}>
                    <Input
                        id="adornment-amount"
                        type="number"
                        value={item.value}
                        onChange={(e) => handleChange(e)}
                        startAdornment={<InputAdornment position="start">R$</InputAdornment>}
                    />
                </FormControl>
                <div style={{ display: "flex", justifyContent: 'space-around' }}>
                    <FormControlLabel
                        control={
                            <Checkbox
                                checked={item.paid}
                                onChange={(e) => handleChange(e)}
                                value="checkedG"
                                color="primary"
                            />
                        }
                        label="Pago"
                        style={{ marginTop: 25 }}
                    />
                    <MuiPickersUtilsProvider utils={DateFnsUtils}>
                        <KeyboardDatePicker
                            margin="normal"
                            id="mui-pickers-date"
                            label="Date picker"
                            value={
                                item.date ?
                                    moment(item.date.toDate())
                                    : ''}
                            onChange={(e) => handleChange(e)}
                            KeyboardButtonProps={{
                                'aria-label': 'change date',
                            }}
                            style={{ marginTop: 25 }}
                        />
                    </MuiPickersUtilsProvider>
                </div>
                <Button variant="contained" color="primary" style={{ marginLeft: "auto", marginTop: 25 }}>
                    Salvar
                </Button>
            </form>
        </Paper>
    </Modal>
)

export default ModalEdit
